INSERT INTO Polizza VALUES(1,'Polizza di Viaggio Completa','La Polizza di Viaggio Completa è la scelta ideale per chi desidera massima tranquillità durante il proprio viaggio. Questa polizza offre una copertura estesa che si estende ben oltre le situazioni di base, garantendoti una protezione completa.
INSERT INTO Polizza VALUES(2,'Polizza di Viaggio Covid19','La Polizza di Viaggio con Copertura COVID-19 è stata progettata per affrontare le sfide specifiche legate alla pandemia di COVID-19 durante i tuoi viaggi. Questa polizza offre una copertura speciale per garantire la tua sicurezza e la tranquillità durante il viaggio in un periodo di incertezza.
INSERT INTO Polizza VALUES(3,'Polizza di Viaggio Pets','Descrizione: La Polizza di Viaggio per Animali Domestici è pensata per i viaggiatori che desiderano portare con sé i loro amici a quattro zampe. Questa polizza fornisce una copertura speciale per la salute e la sicurezza dei tuoi animali domestici durante il viaggio.
INSERT INTO Polizza VALUES(0,'Polizza di Viaggio Base','La Polizza di Viaggio Base è progettata per offrire una copertura essenziale e affidabile durante il tuo viaggio. Che tu stia pianificando una vacanza breve o un''avventura più lunga, questa polizza ti fornisce la tranquillità di sapere che sei protetto contro una serie di imprevisti.
INSERT INTO DESCRIZIONE VALUES (0, 'Rilassati al Mare', 'Goditi una vacanza rilassante al mare con spiagge di sabbia dorata e acque cristalline.');
INSERT INTO DESCRIZIONE VALUES (1, 'Esplora una Città Vibrante', 'Scopri una città piena di vita, con monumenti storici, ristoranti gourmet e vita notturna vivace.');
INSERT INTO DESCRIZIONE VALUES (2, 'Avventura in Montagna', 'Fai trekking tra le montagne, respira l''aria fresca e ammira panorami mozzafiato.');
INSERT INTO DESCRIZIONE VALUES (3, 'Vacanza Culturale', 'Immergiti nella cultura locale con visite a musei, gallerie d''arte e siti storici.');
INSERT INTO DESCRIZIONE VALUES (4, 'Rilassamento al Mare', 'Trascorri giornate di puro relax sulla spiaggia e rilassati in resort di lusso.');
INSERT INTO DESCRIZIONE VALUES (5, 'Esplorazione Urbana', 'Passeggia per le strade affollate, assapora la cucina locale e scopri segreti nascosti della città.');
INSERT INTO DESCRIZIONE VALUES (6, 'Escursioni in Montagna', 'Affronta escursioni impegnative e conquista le vette più alte.');
INSERT INTO DESCRIZIONE VALUES (7, 'Viaggio Enogastronomico', 'Delizia il tuo palato con prelibatezze locali e degusta vini pregiati.');
INSERT INTO DESCRIZIONE VALUES (8, 'Divertimento in Spiaggia', 'Partecipa a sport acquatici, feste in spiaggia e attività all''aperto.');
INSERT INTO DESCRIZIONE VALUES (9, 'Arte e Cultura Urbana', 'Esplora gallerie d''arte contemporanea, teatri e quartieri culturali.');
INSERT INTO DESCRIZIONE VALUES (10, 'Relax Costiero', 'Goditi il suono delle onde e le brezze marine in un tranquillo rifugio sulla costa.');
INSERT INTO DESCRIZIONE VALUES (11, 'Esplorazione Storica', 'Scopri la storia antica della regione visitando siti archeologici e castelli medievali.');
INSERT INTO DESCRIZIONE VALUES (12, 'Sfide in Montagna', 'Affronta percorsi avventurosi, arrampicate su roccia e gite in bicicletta in montagna.');
INSERT INTO DESCRIZIONE VALUES (13, 'Viaggio Gastronomico', 'Assaggia la cucina locale attraverso tour culinari guidati e lezioni di cucina.');
INSERT INTO DESCRIZIONE VALUES (14, 'Spiaggia e Sport Acquatici', 'Pratica sport acquatici come il surf, il nuoto e il kayak nelle acque cristalline.');
INSERT INTO DESCRIZIONE VALUES (15, 'Vita Notturna Cittadina', 'Esperienza notti indimenticabili con club, bar e spettacoli in città.');
INSERT INTO DESCRIZIONE VALUES (16, 'Percorsi Panoramici in Montagna', 'Segui percorsi panoramici tra valli e cime montane mozzafiato.');
INSERT INTO DESCRIZIONE VALUES (17, 'Esperienza Teatrale', 'Assisti a spettacoli teatrali, concerti e opere d''arte in città culturali.');
INSERT INTO DESCRIZIONE VALUES (18, 'Soggiorno di Lusso in Riva al Mare', 'Vivi in ​​un resort di lusso con servizio personalizzato a due passi dalla spiaggia.');
INSERT INTO DESCRIZIONE VALUES (19, 'Esplorazione Architettonica', 'Ammira l''architettura unica delle città con edifici storici e moderni.');
INSERT INTO DESCRIZIONE VALUES (20, 'Ski e Neve', 'Scia sulle piste innevate e rilassati in accoglienti chalet di montagna.');
INSERT INTO DESCRIZIONE VALUES (21, 'Cultura e Musica', 'Immergiti nella scena musicale e culturale della città con concerti e festival.');
INSERT INTO DESCRIZIONE VALUES (22, 'Resort All-Inclusive', 'Goditi un soggiorno senza pensieri in un resort all-inclusive con tutte le comodità.');
INSERT INTO DESCRIZIONE VALUES (23, 'Tour Enologici', 'Esplora le cantine locali e degusta i migliori vini e prodotti tipici della regione.');
INSERT INTO DESCRIZIONE VALUES (24, 'Avventure Subacquee', 'Scopri il mondo sottomarino con immersioni subacquee e snorkeling.');
INSERT INTO DESCRIZIONE VALUES (25, 'Arte Moderna e Contemporanea', 'Visita musei d''arte moderna e contemporanea nelle città d''avanguardia.');
INSERT INTO DESCRIZIONE VALUES (26, 'Relax Termale in Montagna', 'Rilassati nelle sorgenti termali naturali in un ambiente di montagna sereno.');
INSERT INTO DESCRIZIONE VALUES (27, 'Scoperta Storica e Religiosa', 'Esplora siti storici e luoghi di culto in città ricche di storia.');
INSERT INTO DESCRIZIONE VALUES (28, 'Spiaggia e Yoga', 'Rigenera corpo e mente con lezioni di yoga sulla spiaggia al sorgere del sole.');
INSERT INTO DESCRIZIONE VALUES (29, 'Avventura in Montagna Estiva', 'Esplora le montagne durante l''estate con escursioni e sport all''aperto.');
INSERT INTO DESCRIZIONE VALUES (30, 'Hotel Bella Vista', 'Un hotel di lusso con vista panoramica sulla baia.');
